import React ,{useState}from "react"
import uniquid from "uniqid"
function  AgregarUsuarios(){
//hooks

const [nombre , setNombre]= useState("");
const [email , setEmail]= useState("");
const [telefono , setTeleono]= useState("");

    function agregarUsuario(){
let usuario={
    nombre: nombre,
    email: email,
    telefono: telefono,
    idUsuario: uniquid()
}
axios.post("/api/usuario/agregarUsuario", usuario).then(res=>{
    alert(res.data)
}).then(err=>{console.log(err)})
    }


    return (

        <div className="container">
          <div className="row">
            <div className="col-sm-6 offset-3">
               <h2 className="mt-4">Crear nuevo usuario</h2>
            </div>
          </div>
          <div className="row">
            <div className="col-sm-6 offset-3">
               <div className="mb-3">
                <label htmlFor="nombre" className="form-label">Nombre</label>
                <input type="text" className="form-control" value={nombre} onChange={(e)=>{setNombre(e.target.value)}}></input>
               </div>
               <div className="mb-3">
                <label htmlFor="email" className="form-label">Email</label>
                <input type="email" className="form-control"value={email} onChange={(e)=>{setEmail(e.target.value)}}></input>
               </div>
               <div className="mb-3">
                <label htmlFor="telefono" className="form-label">Telefono</label>
                <input type="text" className="form-control" value={telefono} onChange={(e)=>{setTeleono(e.target.value)}}></input>
               </div>
               <button onClick={agregarUsuario} className="btn btn-success">Guardar usuario</button>
            </div>
          </div>
        </div>
    )
}

export default AgregarUsuarios
